TRICKSTER
<Hide Turns>

<Custom Apply Effect>
target._trickStartingTP = target.tp;
</Custom Apply Effect>

<Custom Remove Effect>
var diff = target.tp - target._trickStartingTP;
if (diff > 0)
{
   target.gainTp(-diff);
   origin.gainTp(diff);
}
target._trickStartingTP = undefined;
</Custom Remove Effect>

// Lightning Rod:
// If attack contains Thunder element,
// replenish + reflect @ turn end,
// with an added MAT buff.
<Custom Respond Effect>
if (this.getItemElements().contains(5)) {
  if (!$gameTemp._lightRod) $gameTemp._lightRod = 0;
  $gameTemp._lightRod += value;
  target.startAnimation(125, 0, 0);
}
</Custom Respond Effect>

<Custom Turn End Effect>
if ($gameTemp._lightRod) $gameSwitches.setValue(21,true);
  // this switch triggers a page of the Troop
  // The forced skill will set lightRod to undefined
</Custom Turn End Effect>

// 66% HP: physical attacks may inflict Thunder Weakness on attacker
// 33% HP: water attacks may inflict Confusion on target
<Custom Establish Effect>
var hpPct = (user.hp / user.mhp);
var elems = this.getItemElements();
if (hpPct <= 0.66) {
   if (this.isPhysical() || elems.contains(1))
      if (Math.random(1) <= 0.2) target.addState(115);
   if (hpPct <= 0.33) {
      if (elems.contains(6))
         if (Math.random(1) <= 0.2) target.addState(62);
   }
}
</Custom Establish Effect>
